
function openBurger(){var menu=document.getElementsByClassName("nav");menu[0].classList.add("translate0");}
function closeBurger(){var menu=document.getElementsByClassName("nav");menu[0].classList.remove("translate0");}
document.addEventListener("DOMContentLoaded",function(event){var menu=document.getElementsByClassName("nav");var menuElement=menu[0];var menuElementFirstOfChild=menuElement.firstChild;var li=document.createElement("li");menuElement.insertBefore(li,menuElementFirstOfChild);menuElementFirstOfChild.innerHTML="<i class='fa fa-times' aria-hidden='true'></i>";menuElementFirstOfChild.classList.add("burger__cross");var att=document.createAttribute("onclick");att.value="closeBurger()";menuElementFirstOfChild.setAttributeNode(att);});